import{_ as e,c as a,o as s,a as o}from"./app.45f7e039.js";const m=JSON.parse('{"title":"FAQ","description":"","frontmatter":{},"headers":[{"level":2,"title":"how to backup and restore the database?","slug":"how-to-backup-and-restore-the-database","link":"#how-to-backup-and-restore-the-database","children":[]},{"level":2,"title":"how to configure the email server in Docker?","slug":"how-to-configure-the-email-server-in-docker","link":"#how-to-configure-the-email-server-in-docker","children":[]},{"level":2,"title":"how to use MySQL in Docker?","slug":"how-to-use-mysql-in-docker","link":"#how-to-use-mysql-in-docker","children":[]},{"level":2,"title":"how to build a Docker image by myself?","slug":"how-to-build-a-docker-image-by-myself","link":"#how-to-build-a-docker-image-by-myself","children":[]},{"level":2,"title":"How to view the API and Jinja2 template variables supported by the current framework?","slug":"how-to-view-the-api-and-jinja2-template-variables-supported-by-the-current-framework","link":"#how-to-view-the-api-and-jinja2-template-variables-supported-by-the-current-framework","children":[]},{"level":2,"title":"how to submit a bug issue?","slug":"how-to-submit-a-bug-issue","link":"#how-to-submit-a-bug-issue","children":[]},{"level":2,"title":"Which requests are necessary for QD?","slug":"which-requests-are-necessary-for-qd","link":"#which-requests-are-necessary-for-qd","children":[]},{"level":2,"title":"Will my username and password be revealed?","slug":"will-my-username-and-password-be-revealed","link":"#will-my-username-and-password-be-revealed","children":[]},{"level":2,"title":"Prompt warning message: Connect Redis falied: Error 10061","slug":"prompt-warning-message-connect-redis-falied-error-10061","link":"#prompt-warning-message-connect-redis-falied-error-10061","children":[]},{"level":2,"title":"Prompt warning message: Import PyCurl module falied","slug":"prompt-warning-message-import-pycurl-module-falied","link":"#prompt-warning-message-import-pycurl-module-falied","children":[]}],"relativePath":"guide/faq.md"}'),n={name:"guide/faq.md"},r=o(`<h1 id="faq" tabindex="-1">FAQ <a class="header-anchor" href="#faq" aria-hidden="true">#</a></h1><h2 id="how-to-backup-and-restore-the-database" tabindex="-1">how to backup and restore the database? <a class="header-anchor" href="#how-to-backup-and-restore-the-database" aria-hidden="true">#</a></h2><p>QD uses <strong>sqlite3</strong> as the framework database by default, and the <code>database.db</code> file is saved in <code>config</code> directory. When deploying with a Docker container, you can use the <code>docker cp</code> command to back up the database file, and then use the <code>docker cp</code> command to restore the database file in the new container.</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># database backup</span></span>
<span class="line"><span style="color:#FFCB6B;">docker</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">cp</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">container_name:/usr/src/app/config/database.db</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">.</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># Database recovery</span></span>
<span class="line"><span style="color:#FFCB6B;">docker</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">cp</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">database.db</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">container_name:/usr/src/app/config/</span></span>
<span class="line"></span></code></pre></div><h2 id="how-to-configure-the-email-server-in-docker" tabindex="-1">how to configure the email server in Docker? <a class="header-anchor" href="#how-to-configure-the-email-server-in-docker" aria-hidden="true">#</a></h2><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">docker</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">run</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-d</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--name</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">qiandao</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-p</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">8923</span><span style="color:#C3E88D;">:</span><span style="color:#F78C6C;">80</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-v</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$(</span><span style="color:#82AAFF;">pwd</span><span style="color:#89DDFF;">)</span><span style="color:#C3E88D;">/qiandao/config:/usr/src/app/config</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--env</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">MAIL_SMTP=</span><span style="color:#A6ACCD;">$STMP_Server_ </span><span style="color:#C3E88D;">--env</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">MAIL_PORT=</span><span style="color:#A6ACCD;">$Mailbox_server_port </span><span style="color:#C3E88D;">--env</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">MAIL_USER=</span><span style="color:#A6ACCD;">$Username </span><span style="color:#C3E88D;">--env</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">MAIL_PASSWORD=</span><span style="color:#A6ACCD;">$Password </span><span style="color:#C3E88D;">--env</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">DOMAIN=</span><span style="color:#A6ACCD;">$Domain </span><span style="color:#C3E88D;">a76yyyy/qiandao</span></span>
<span class="line"></span></code></pre></div><h2 id="how-to-use-mysql-in-docker" tabindex="-1">how to use MySQL in Docker? <a class="header-anchor" href="#how-to-use-mysql-in-docker" aria-hidden="true">#</a></h2><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">docker</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">run</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-d</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--name</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">qiandao</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-p</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">8923</span><span style="color:#C3E88D;">:</span><span style="color:#F78C6C;">80</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-v</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$(</span><span style="color:#82AAFF;">pwd</span><span style="color:#89DDFF;">)</span><span style="color:#C3E88D;">/qiandao/config:/usr/src/app/config</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--ENV</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">DB_TYPE=mysql</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--ENV</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">JAWSDB_MARIA_URL=mysql://</span><span style="color:#A6ACCD;">$username</span><span style="color:#C3E88D;">:</span><span style="color:#A6ACCD;">$password</span><span style="color:#C3E88D;">@</span><span style="color:#A6ACCD;">$hostname</span><span style="color:#C3E88D;">:</span><span style="color:#A6ACCD;">$port</span><span style="color:#C3E88D;">/</span><span style="color:#A6ACCD;">$database_name</span><span style="color:#C3E88D;">?auth_plugin=</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">a76yyyy/qiandao</span></span>
<span class="line"></span></code></pre></div><h2 id="how-to-build-a-docker-image-by-myself" tabindex="-1">how to build a Docker image by myself? <a class="header-anchor" href="#how-to-build-a-docker-image-by-myself" aria-hidden="true">#</a></h2><p>Please refer to the build file <a href="https://github.com/qiandao-today/qiandao/blob/master/Dockerfile" target="_blank" rel="noreferrer">Dockerfile</a> of this image.</p><h2 id="how-to-view-the-api-and-jinja2-template-variables-supported-by-the-current-framework" tabindex="-1">How to view the API and Jinja2 template variables supported by the current framework? <a class="header-anchor" href="#how-to-view-the-api-and-jinja2-template-variables-supported-by-the-current-framework" aria-hidden="true">#</a></h2><p>Please access the home page of the framework, and then click the <code>Common API/Filter</code> button in the upper left corner to view the API and Jinja2 template variables supported by the current framework.</p><h2 id="how-to-submit-a-bug-issue" tabindex="-1">how to submit a bug issue? <a class="header-anchor" href="#how-to-submit-a-bug-issue" aria-hidden="true">#</a></h2><p>Please enable <code>Debug</code> mode after encountering a problem, and then submit detailed error information to <a href="https://github.com/qiandao-today/qiandao/issues" target="_blank" rel="noreferrer">Issue</a>.</p><h2 id="which-requests-are-necessary-for-qd" tabindex="-1">Which requests are necessary for QD? <a class="header-anchor" href="#which-requests-are-necessary-for-qd" aria-hidden="true">#</a></h2><p>Empirically, the following requests are necessary:</p><ul><li>login page</li><li>POST to login page</li><li>Issue a request for username, password</li><li>The page that caused the page to jump after sending</li><li>The first page before and after page turning</li></ul><h2 id="will-my-username-and-password-be-revealed" tabindex="-1">Will my username and password be revealed? <a class="header-anchor" href="#will-my-username-and-password-be-revealed" aria-hidden="true">#</a></h2><p>In order to help users initiate requests, user names and passwords still need to be recorded. This can only rely on the self-discipline of server maintainers to ensure the security of back-end data. But in the framework design, each user is encrypted with a secure key when storing. Encrypting user data with a key can ensure that user data cannot be decrypted only by obtaining the database. (Encrypted user data includes templates uploaded by users, variables set by users for tasks, etc.)</p><p>If you are still worried, you can build the QD framework by yourself, download the template and run it on your own server.</p><h2 id="prompt-warning-message-connect-redis-falied-error-10061" tabindex="-1">Prompt warning message: <code>Connect Redis falied: Error 10061</code> <a class="header-anchor" href="#prompt-warning-message-connect-redis-falied-error-10061" aria-hidden="true">#</a></h2><p>QD uses <code>redis</code> as a flow limiting tool. If the <code>redis</code> service is not installed, the framework will prompt the following warning message.</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">W xxxxxx xx:xx:xx qiandao.RedisDB redisdb:</span><span style="color:#F78C6C;">28</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> Connect Redis falied: Error </span><span style="color:#F78C6C;">10061</span><span style="color:#A6ACCD;"> connecting to localhost:</span><span style="color:#F78C6C;">6379.</span><span style="color:#A6ACCD;"> No connection could be made because the target machine actively refused it.</span></span>
<span class="line"></span></code></pre></div><p>However, <code>redis</code> is not required in this framework, if you don&#39;t need to use the <code>flow-limiting</code> feature, you can ignore the warning message.</p><blockquote><p>It is recommended to use <code>Docker compose</code> to deploy the QD framework, and the Docker compose configuration already installs the redis container by default.</p></blockquote><h2 id="prompt-warning-message-import-pycurl-module-falied" tabindex="-1">Prompt warning message: <code>Import PyCurl module falied</code> <a class="header-anchor" href="#prompt-warning-message-import-pycurl-module-falied" aria-hidden="true">#</a></h2><p>QD uses the <code>pycurl</code> module to send HTTP proxy requests. If the <code>pycurl</code> module is not installed, the framework will prompt the following warning message:</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">W xxxxxx xx:xx:xx qiandao.Http.Fetcher fetcher:</span><span style="color:#F78C6C;">34</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> Import PyCurl module falied: No module named </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">pycurl</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"></span></code></pre></div><p>However, <code>pycurl</code> is not required in this framework, if you don&#39;t need to use the <code>proxy</code> function, you can ignore the warning message.</p><blockquote><p>The <code>pycurl</code> module is cumbersome to install on the Windows system, and the <code>libcurl</code> library needs to be installed. For the specific installation method, please refer to <a href="http://pycurl.io/docs/latest/install.html" target="_blank" rel="noreferrer">pycurl official documentation</a>.</p><p>It is recommended to use a container or linux system to deploy the QD framework. The docker container has a pre-installed Curl environment, and the pycurl module is installed by default</p></blockquote>`,30),l=[r];function t(p,i,c,d,h,y){return s(),a("div",null,l)}const C=e(n,[["render",t]]);export{m as __pageData,C as default};
